package com.semi.main;

public class IndexNum {
	private int number;
	private int value;
	public IndexNum() {
		super();
		// TODO Auto-generated constructor stub
	}
	public IndexNum(int number, int value) {
		super();
		this.number = number;
		this.value = value;
	}
	public int getNumber() {
		return number;
	}
	public void setNumber(int number) {
		this.number = number;
	}
	public int getValue() {
		return value;
	}
	public void setValue(int value) {
		this.value = value;
	}
	
	
}
